function dm_send() {
	if (!$("#dm_input").val()) {
		$("#page_dm").hide()
		return
	}
	socket.emit("command", {
		list: ["dm2", {
			target: $("#dm_guid").val(),
			text: $("#dm_input").val()
		}]
	})
	$("#dm_input").val("")
	$("#page_dm").hide()
	$("#chat_message").focus()
}
function registerAccount() {
	if (!$("#acc_guid").val()) {
		$("#page_register").hide()
		return
	}
	if (!$("#acc_name").val()) {
		$("#page_register").hide()
		return
	}
    $("#page_register").hide();
	socket.emit("register",{name:$("#acc_name").val(),guid:$("#acc_guid").val()})
}
